import React, { useState } from 'react';
import { useAuthStore } from '../../src/stores/authStore';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { authAPI } from '../../src/services/api';
import {
  BuildingOfficeIcon,
  UsersIcon,
  CalculatorIcon,
  Cog8ToothIcon,
} from '@heroicons/react/24/outline';

const Settings: React.FC = () => {
  const { user } = useAuthStore();
  const queryClient = useQueryClient();

  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [passwordChangeMessage, setPasswordChangeMessage] = useState('');
  const [passwordChangeError, setPasswordChangeError] = useState('');

  const changePasswordMutation = useMutation({
    mutationFn: authAPI.changePassword,
    onSuccess: () => {
      setPasswordChangeMessage('Password changed successfully!');
      setPasswordChangeError('');
      setCurrentPassword('');
      setNewPassword('');
      setConfirmNewPassword('');
      queryClient.invalidateQueries({ queryKey: ['currentUser'] });
    },
    onError: (error: any) => {
      setPasswordChangeMessage('');
      setPasswordChangeError(error.response?.data?.message || 'Failed to change password.');
    },
  });

  const handleChangePassword = (e: React.FormEvent) => {
    e.preventDefault();
    setPasswordChangeMessage('');
    setPasswordChangeError('');

    if (newPassword !== confirmNewPassword) {
      setPasswordChangeError('New passwords do not match.');
      return;
    }

    if (newPassword.length < 6) {
      setPasswordChangeError('New password must be at least 6 characters long.');
      return;
    }

    changePasswordMutation.mutate({ currentPassword, newPassword });
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Settings</h1>
        <p className="text-gray-600">Configure system settings and user preferences.</p>
      </div>

      {/* General Settings */}
      <div className="card p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">General Settings</h3>
        <div className="grid grid-cols-1 gap-6 md:grid-cols-2">
          <div>
            <label htmlFor="companyName" className="block text-sm font-medium text-gray-700">
              Company Name
            </label>
            <input
              type="text"
              id="companyName"
              value={user?.company?.name || ''}
              disabled
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
          <div>
            <label htmlFor="userEmail" className="block text-sm font-medium text-gray-700">
              Your Email
            </label>
            <input
              type="email"
              id="userEmail"
              value={user?.email || ''}
              disabled
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
        </div>
      </div>

      {/* Change Password */}
      <div className="card p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Change Password</h3>
        <form onSubmit={handleChangePassword} className="space-y-4">
          <div>
            <label htmlFor="currentPassword" className="block text-sm font-medium text-gray-700">
              Current Password
            </label>
            <input
              type="password"
              id="currentPassword"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
              required
            />
          </div>
          <div>
            <label htmlFor="newPassword" className="block text-sm font-medium text-gray-700">
              New Password
            </label>
            <input
              type="password"
              id="newPassword"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
              required
            />
          </div>
          <div>
            <label htmlFor="confirmNewPassword" className="block text-sm font-medium text-gray-700">
              Confirm New Password
            </label>
            <input
              type="password"
              id="confirmNewPassword"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary-500 focus:ring-primary-500 sm:text-sm"
              required
            />
          </div>
          {passwordChangeMessage && (
            <p className="text-success-600 text-sm">{passwordChangeMessage}</p>
          )}
          {passwordChangeError && (
            <p className="text-error-600 text-sm">{passwordChangeError}</p>
          )}
          <button
            type="submit"
            className="btn btn-primary"
            disabled={changePasswordMutation.isPending}
          >
            {changePasswordMutation.isPending ? 'Changing...' : 'Change Password'}
          </button>
        </form>
      </div>

      {/* Other Settings Sections (Placeholders) */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        <div className="card p-6 flex items-center space-x-4">
          <BuildingOfficeIcon className="h-8 w-8 text-primary-500" />
          <div>
            <h3 className="text-lg font-medium text-gray-900">Company Profile</h3>
            <p className="text-gray-600 text-sm">Manage your company's details and branding.</p>
          </div>
        </div>
        <div className="card p-6 flex items-center space-x-4">
          <UsersIcon className="h-8 w-8 text-primary-500" />
          <div>
            <h3 className="text-lg font-medium text-gray-900">User Management</h3>
            <p className="text-gray-600 text-sm">Add, edit, and manage user accounts and roles.</p>
          </div>
        </div>
        <div className="card p-6 flex items-center space-x-4">
          <CalculatorIcon className="h-8 w-8 text-primary-500" />
          <div>
            <h3 className="text-lg font-medium text-gray-900">Tax Configuration</h3>
            <p className="text-gray-600 text-sm">Set up tax rates and compliance settings.</p>
          </div>
        </div>
        <div className="card p-6 flex items-center space-x-4">
          <Cog8ToothIcon className="h-8 w-8 text-primary-500" />
          <div>
            <h3 className="text-lg font-medium text-gray-900">System Preferences</h3>
            <p className="text-gray-600 text-sm">Configure general system behavior and defaults.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
